INSERT INTO `tb_general` (`id`, `title`, `content`, `moderator`, `time`) VALUES
(1, 'about', 'এনভায়রনমেন্ট কনজারভেশন নেটওয়ার্ক বাংলাদেশ সংক্ষেপে ইসিএন-বিডি, মূলত বাংলা ভাষায় পরিবেশ সম্পর্কিত বিভিন্ন সাম্প্রতিক গবেষণা, ডকুমেন্টরি, তথ্যাবলী, সংবাদ ইত্যাদি প্রকাশ করে থাকে। এছাড়াও প্রকাশ করে থাকে বিভিন্ন গুণীজনদের অভিজ্ঞতা নির্ভর নানা ধরণের তথ্যবহুল নিবন্ধ ।<br><br>এনভায়রনমেন্ট কনজারভেশন নেটওয়ার্কের কর্মকান্ড পরিচালিত হয় ঢাকা, বাংলাদেশ থেকে। তাছাড়া দেশের বিভিন্ন প্রান্তে ছড়িয়ে ছিটিয়ে থাকা আমাদের এক ঝাঁক নিবেদিত কর্মীও প্রতিনিয়ত চেষ্টা করে যাচ্ছেন তাদের বস্তুনিষ্ঠ চিন্তা ও লেখনীর মাধ্যমে পরিবেশ বিষয়ক নানা বিষয় উপস্থাপন করার।<br>', 'Moderator', '1591362168'),
(2, 'footer', 'সকল সত্ত্ব সংরক্ষিত © এনভায়রনমেন্ট কনজারভেশন নেটওয়ার্ক বাংলাদেশ', 'Murshid', '1590047956'),
(3, 'editor', '<br>', 'Admin', '1626687136'),
(4, 'sub-editor', '<p>        মোছা তাহসিনা খাতুন<br>        মো আব্দুল্লাহ আল মামুন<br>        মো বায়েজিদ শেখ<br><br></p>', 'Moderator', '1591450774'),
(5, 'policy', '<p>আমাদের ওয়েবসাইট দেখার জন্য আপনাকে আন্তরিক ধন্যবাদ। এনভায়রনমেন্ট কনজারভেশন নেটওয়ার্ক ওয়েব সাইট এ পরিবেশ দূষন ও নিয়ন্ত্রণ, জলবায়ু পরিবর্তন, বন ও বন্যপ্রাণী, টেকসই উন্নয়ন ও সবুজ অর্থনীতি, নবায়নযোগ্য শক্তি, স্বাস্থ্য ও নদী সেবা সংক্রান্ত হালনাগাদ তথ্য সরবরাহের একটি উদ্যোগ। এই ওয়েবসাইটটি ব্যবহার করার জন্য আপনাকে অবশ্যই কিছু শর্তাবলী মেনে চলতে হবে, যা আপনি এই সাইটে প্রবেশ করা মাত্রই প্রযোজ্য।<br> <br><b>শর্তাবলী:</b><br>১. এই ওয়েবসাইটের তথ্য এবং লিংককৃত ওয়েবসাইটের তথ্য ব্যবহার করার ফলে প্রত্যক্ষ্য বা অপ্রত্যক্ষভাবে কোন ক্ষতির সম্মুখীন হলে তার জন্য কোন দায়দায়িত্ব এনভায়রনমেন্ট কনজারভেশন নেটওয়ার্ক (ইসিএনবিডি) গ্রহণ করবে না।<br>২. এই ওয়েবসাইটের কর্মকাণ্ডের কোন ধরনের অবিচ্ছিন্নতার জন্য নিশ্চয়তা প্রদান করবে না।<br> <br><b>কপিরাইট:</b><br>এই ওয়েবসাইটে যেসব কনটেন্ট, তথ্যাদি এবং ট্রেডমার্কস প্রদর্শিত হচ্ছে কিংবা এই ওয়েবসাইটের সাথে লিংককৃত অন্যান্য ওয়েবসাইটের সমস্ত তথ্যাদির কপিরাইট তাদের নিজস্ব এবং তা কপিরাইট আইন (সংশোধিত ২০০৫ সালে) এর মাধ্যমে সংরক্ষিত।<br> <br><b>তথ্যের কাজ এবং প্রিন্ট:</b><br>এই ওয়েবসাইটের সকল ব্যবহারকারী ওয়েবসাইটে প্রদর্শিত সকল তথ্যের কোন রকম পরিমার্জন, সংযুক্তিকরণ এবং সংশোধন ব্যতীত প্রিন্ট করতে পারবেন। কিন্তু এই পোটার্লে প্রকাশিত কোন তথ্য যা বাংলাদেশ সরকারের নয় এবং যাতে অন্য কোনো সংস্থার কপিরাইট রয়েছে সেক্ষেত্রে সে সংস্থার অনুমতি গ্রহণ করতে হবে।<br> <br><b>প্রবেশাধিকার:</b><br>কোন বিশেষ ব্যক্তি অথবা ইন্টারনেট থেকে  ব্রাউজকৃত কোন বিশেষ ঠিকানাকে কোন প্রকার কারণ দর্শন ব্যতীত এ ওয়েবসাইটে প্রবেশাধিকারে নিষেধাজ্ঞা আরোপ করা হতে পারে।<br> <br><b>নীতিমালা সংযোজন এবং পরিবর্তনের নোটিশ:</b><br>কোন প্রকার নোটিশ ব্যতীত যে কোন সময় এই নীতিমালা সংশোধন করা হতে পারে। যেকোন তথ্যাদি যা বর্তমান নীতিমালার মাধ্যমে সংরক্ষণ করা হয়েছিল তা এসব শর্তাবলী মেনে চলবে। নীতিমালার পরিবর্তনের পর যদি কোন তথ্যাদি সংগ্রহ করা হয় তা অবশ্যই পরিবর্তিত নীতিমালার মাধ্যমে পালনীয় হবে। <br> <br><b>বিচার ব্যবস্থা:</b><br>এইসব শর্তাবলী বাংলাদেশের আইন  অনুযায়ী পরিচালিত হচ্ছে। কোন ধরনের আপত্তি বাংলাদেশের বিচার ব্যবস্থার মাধ্যমে মীমাংসিত হবে। সকল শর্তাবলী পাঠকগনকে মেনে চলার আহবান করা যাচ্ছে অন্যথায় প্রচলিত আইনে ব্যবস্থা নেয়া হতে পারে।<br></p>', 'Murshid Raj', '1591264248'),
